<html><head>    <meta charset="utf80">    <title>Mehmet TÜRKER - Tema Seçme Sistemei</title></head><body><form action="index.php" method="POST">    <select name="tema">        <option value="a">a</option>        <option value="b">b</option>        <option value="c">c</option>        <option value="d">d</option>        <option value="e">e</option>        <option value="f">f</option>        <option value="g">g</option>        <option value="h">h</option>    </select>    <input type="submit" value="Tema Seç"></form>    <?php        if($_POST['tema']){            $tema = $_POST['tema'];            setcookie("tema", time() - 3600);            setcookie("tema", $tema, time() + (60 * 60 * 24));        }else {            setcookie("tema", "default", time() + (60*60*24));        }    ?>    Temanız : <font style="color: red; font-style: bold"><?php echo $_COOKIE['tema'];?></font></body></html>